import React, { useEffect, useRef, useState } from "react";
import Compose from "../Compose";
import Message from "../Message";
import moment from "moment";

import "./MessageList.css";
import Toolbar from "../Toolbar";
import GPTService from "../../services/gpt";
import MessageBuilderService from "../../services/message-builder";
import TypingIndicator from "../TypingIndicator";

import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

const MY_USER_ID = "Patient";

export interface Message {
  author: string;
  message: string;
  timestamp: number;
}
export default function MessageList() {
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([
    {
      author: conversationType,
      message:
        "Hi friend! I'm your virtual therapist. How are you feeling today?",
      timestamp: new Date().getTime(),
    },
  ] as Message[]);

  // Stays scrolled to bottom.
  const messagesEndRef = useRef(null);
  useEffect(() => {
    if (messagesEndRef) {
      (messagesEndRef.current as any)?.scrollIntoView({ behavior: "smooth" })
    }
  });
  const addMyMessage = async (value: string) => {
    const newMessage = {
      author: MY_USER_ID,
      message: value,
      timestamp: new Date().getTime(),
    };

    setMessages((previous) => [...previous, newMessage]);
    setLoading(true);
    const withMine = [...messages, newMessage];
    const response = await GPTService.getAIResponse(withMine);
    const responseMessage = {
      author: "Therapist",
      message: response,
      timestamp: new Date().getTime(),
    };
    setMessages((previous) => [...previous, responseMessage]);
    setLoading(false);
  };

  const options = [
    'Intellectual Conversation',
    'Theraputic Conversation',
    'Playful Conversation',
    'Problem-solving Conversation',
    'Open-ended Conversation'
  ];
  const defaultOption = options[0];
  const [option, setOption] = useState(defaultOption);

  return (
    <div className="message-list">
      <Toolbar title="Chat with GPT3" />
      {/* <Dropdown title="Select conversatio type" /> */}
      <h4>What kind of conversation do you want to have?</h4>
      <Dropdown options={options} onChange={(e) => {setOption(e.value); console.log(option)}} value={defaultOption} placeholder="Select conversation type" />

      <div className="message-list-container">{MessageBuilderService.getMessages(messages, MY_USER_ID)}
      {loading && <TypingIndicator />}
      <div ref={messagesEndRef} />
      </div>


      <Compose addMessage={addMyMessage} />
    </div>
  );
}
